sys_script_include.script:
var VendorUtils = Class.create();
VendorUtils.prototype = {
  initialize: function() {},

fetchAllVendors: function() {
        var res = [];
        var gr = new GlideRecord('x_1792507_event_ma_vendor'); 
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                vendor_name: gr.getValue('vendor_name'),
                contact_person: gr.getValue('contact_person'),
                contact_number: gr.getValue('contact_number'),
                available: gr.getDisplayValue('available')
            });
        }
        return JSON.stringify(res);
    },

  getVendorDetails: function(vendorId) {
    var gr = new GlideRecord('x_1792507_event_ma_vendor');
    if (gr.get(vendorId)) {
      return {
        name: gr.getValue('vendor_name'),
        contact: gr.getValue('contact_number'),
        service_type: gr.getValue('email'),
        is_available: gr.getValue('is_vendor_available')
      };
    }
    return null;
  },


  isVendorAvailable: function(vendorId) {
    var gr = new GlideRecord('x_1792507_event_ma_vendor');
    if (gr.get(vendorId)) {
      return gr.getValue('is_vendor_available') == 'true';
    }
    return false;
  },


  getVendorsByEvent: function(eventId) {
    var list = [];
    var gr = new GlideRecord('x_1792507_event_ma_vendor');
    gr.addQuery('event_id', eventId); 
    gr.query();

    while (gr.next()) {
      list.push({
        name: gr.getValue('vendor_name'),
        service_type: gr.getValue('contact_person'),
        contact: gr.getValue('contact_number'),
        is_available: gr.getValue('is_vendor_available')
      });
    }

    return list;
  },

  type: 'VendorUtils'
};
