sys_script_include.script:
var VenueUtils = Class.create();
VenueUtils.prototype = {
    initialize: function() {},

    fetchAllVenues: function() {
        var res = [];
        var gr = new GlideRecord('x_1792507_event_ma_venue'); // Use your actual venue table name
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                venue_name: gr.getValue('venue_name'),
                address: gr.getValue('address'),
                capacity: gr.getValue('capacity'),
                contact_number: gr.getValue('contact_number')
            });
        }
        return JSON.stringify(res);
    },
    type: 'VenueUtils'
};
