sys_script_include.script:
var FeedbackUtils = Class.create();
FeedbackUtils.prototype = {
    initialize: function(eventid) {
        this.eventid = eventid;
    },

    fetchAllFeedback: function() {
        var res = [];
        var gr = new GlideRecord('x_1792507_event_ma_feedback');
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                attendee: gr.getDisplayValue('attendee_id'),
                rating: gr.getValue('rating'),
                comments: gr.getValue('comments')
            });
        }
        return JSON.stringify(res);
    },
    getFeedback: function(id) {
        var res = [];
        var feedback = new GlideRecord('x_1792507_event_ma_feedback');
        feedback.addQuery('number', id);
        feedback.query();
        if (feedback.next()) {
            res.push({
                id: feedback.getDisplayValue(),
                rating: feedback.getValue('rating'),
            });
        }
        return JSON.stringify(res);

    },
    checkFeedback: function(id) {
        var feedback = new GlideRecord('x_1792507_event_ma_feedback');
        feedback.addQuery('number', id);
        feedback.query();
        if (feedback.next()) {
            if (feedback.getValue('rating') == 'excellent' || feedback.getValue('rating') == 'good') {
                return 'positive';
            }
        }
        return 'negative';
    },
    getEventFeedback: function() {
        var res = [];
        var feedback = new GlideRecord('x_1792507_event_ma_feedback');
        feedback.addQuery('event_id', this.eventid);
        feedback.query();
        if (feedback.next()) {
            res.push({
                id: feedback.getDisplayValue(),
                rating: feedback.getValue('rating'),
            });
        }
        return JSON.stringify(res);

    },



    type: 'FeedbackUtils'
};
